import React, { useEffect, useState } from 'react';
import RecipeCard from '../components/RecipeCard';

function Foods() {
  const [foodRecipes, setFoodRecipes] = useState([]);
  const [foodCategories, setFoodCategories] = useState([]);
  const MAX_RECIPES = 12;
  const MAX_CATEGORIES = 5;

  useEffect(() => {
    const foodEndpoint = 'https://www.themealdb.com/api/json/v1/1/search.php?s=';
    const categoriesEndpoint = 'https://www.themealdb.com/api/json/v1/1/list.php?c=list';

    fetch(foodEndpoint)
      .then((res) => res.json())
      .then(({ meals }) => setFoodRecipes(meals));

    fetch(categoriesEndpoint)
      .then((res) => res.json())
      .then(({ meals }) => setFoodCategories(meals));
  }, []);

  return (
    <div>
      { foodCategories.map((category) => {

      }) }
      { foodRecipes.map(({ strMealThumb, strMeal }, index) => {
        if (index < MAX_RECIPES) {
          return (
            <RecipeCard
              key={ strMeal }
              thumb={ strMealThumb }
              name={ strMeal }
              index={ index }
            />
          );
        }
        return null;
      }) }
    </div>
  );
}

export default Foods;
