import React from 'react';
import PropTypes from 'prop-types';
import blackHeartIcon from '../images/blackHeartIcon.svg';
import shareIcon from '../images/shareIcon.svg';

function FavoriteCard(props) {
  const { recipe, updateFavorite, index } = props;
  const {
    id,
    type,
    area,
    category,
    alcoholicOrNot,
    name,
    image,
  } = recipe;

  const removeFromFavorites = (recipeId) => {
    const oldFavorite = JSON.parse(localStorage.getItem('favoriteRecipes'));
    const newFavorite = oldFavorite.filter((favorite) => favorite.id !== recipeId);
    localStorage.setItem('favoriteRecipes', JSON.stringify(newFavorite));
    updateFavorite(newFavorite);
  };

  return (
    <div>
      <img
        src={ image }
        alt={ name }
        data-testid={ `${index}-horizontal-image` }
      />
      { (type === 'comida') ? (
        <p>
          <span>{ area }</span>
          {' - '}
          <span data-testid={ `${index}-horizontal-top-text` }>{ category }</span>
        </p>
      ) : (
        <p data-testid={ `${index}-horizontal-top-text` }>{ alcoholicOrNot }</p>
      ) }
      <p data-testid={ `${index}-horizontal-name` }>{ name }</p>
      <button
        type="button"
        onClick={ () => removeFromFavorites(id) }
        data-testid={ `${index}-horizontal-favorite-btn` }
      >
        <img src={ blackHeartIcon } alt="Remove from favorites" />
      </button>
      <button type="button" data-testid={ `${index}-horizontal-share-btn` }>
        <img src={ shareIcon } alt="Share button" />
      </button>
    </div>
  );
}

FavoriteCard.propTypes = {
  recipe: PropTypes.shape({
    id: PropTypes.string.isRequired,
    type: PropTypes.string.isRequired,
    area: PropTypes.string.isRequired,
    category: PropTypes.string.isRequired,
    alcoholicOrNot: PropTypes.string.isRequired,
    name: PropTypes.string.isRequired,
    image: PropTypes.string.isRequired,
  }).isRequired,
  updateFavorite: PropTypes.func.isRequired,
  index: PropTypes.number.isRequired,
};

export default FavoriteCard;
