import React, { useEffect, useState } from 'react';
import FavoriteCard from '../components/FavoriteCard';
import Header from '../components/Header';

function ReceitasFavoritas() {
  const [favoriteRecipes, setFavoriteRecipes] = useState([]);

  const getAllFavorite = () => {
    setFavoriteRecipes(JSON.parse(localStorage.getItem('favoriteRecipes')));
  };

  const updateFavorite = (updatedRecipes) => {
    setFavoriteRecipes(updatedRecipes);
  };

  useEffect(() => {
    const allFavoriteRecipes = [{
      id: 'id-da-receita',
      type: 'comida',
      area: 'area-da-receita',
      category: 'categoria-da-receita',
      alcoholicOrNot: '',
      name: 'nome-da-receita',
      image: 'https://www.themealdb.com/images/media/meals/wvpsxx1468256321.jpg',
    },
    {
      id: 'id-da-receita2',
      type: 'bebida',
      area: 'area-da-receita2',
      category: 'categoria-da-receita2',
      alcoholicOrNot: 'alcoholic',
      name: 'nome-da-receita2',
      image: 'https://www.thecocktaildb.com/images/media/drink/5noda61589575158.jpg',
    }];

    localStorage.setItem('favoriteRecipes', JSON.stringify(allFavoriteRecipes));

    getAllFavorite();
  }, []);

  return (
    <div>
      <Header titulo="Receitas Favoritas" />
      { favoriteRecipes.map((recipe) => (
        <FavoriteCard key={ recipe.name } recipe={ recipe } updateFavorite={ updateFavorite } />
      )) }
    </div>
  );
}

export default ReceitasFavoritas;
